CREATE OR REPLACE FORCE VIEW pontis.v_oslastinsp (brkey,inspdate) AS
(select brkey, inspdate  from inspevnt i1  where osinspdone = '1' and        to_char(inspdate, 'yyyy') > '1901'  UNION  select brkey, oslastinsp  from inspevnt i2  where to_char(oslastinsp, 'yyyy') > '1901' and        oslastinsp not in (select inspdate from inspevnt i3        where i3.osinspdone = '1' and i3.brkey = i2.brkey) and        oslastinsp is not null)
 ;